from datetime import datetime

try: 
  for record in records:
    cc_dict = record.value['credit_card']
    log.info("========= cc_dict: {}",cc_dict)
    cc = str(cc_dict['card_number'])
    log.info("********* cc: {}",cc)

    if cc == '':
      error.write(record, "Payment type was CRD, but credit card was null")
      continue

    tr_datetime_object = datetime.strptime(str(record.value['credit_card']['transaction_date']), '%m/%d/%Y')
    log.info(" ........ {}",tr_datetime_object)

    cc_type = ''
    if cc.startswith('4'):
      cc_type = 'Visa'
    elif cc.startswith(('51','52','53','54','55')):
      cc_type = 'MasterCard'
    elif cc.startswith(('34','37')):
      cc_type = 'AMEX'
    elif cc.startswith(('300','301','302','303','304','305','36','38')):
      cc_type = 'Diners Club'
    elif cc.startswith(('6011','65')):
      cc_type = 'Discover'
    elif cc.startswith(('2131','1800','35')):
      cc_type = 'JCB'
    else:
      cc_type = 'Other'

    record.value['credit_card_type'] = cc_type

    output.write(record)
except Exception as e:
  error.write(record, e.message)
